'  p_param  in apex_plugin.t_item_render_param,',
'  p_result in out nocopy apex_plugin.t_item_render_result)',
'AS',
'  v_result          apex_plugin.t_page_item_render_result;',
'  v_function        varchar2(128);',
'  v_function2       varchar2(128);',
'  v_js_var          varchar2(128);',
'  v_mask            p_item.attribute_01%TYPE := p_item.attribute_01; -- mask',
'  v_reverse         p_item.attribute_02%TYPE := p_item.attribute_02; -- reverse',
'  v_clearIfNotMatch p_item.attribute_03%TYPE := p_item.attribute_03; -- clearIfNotMatch',
'  v_selectOnFocus   p_item.attribute_05%TYPE := (p_item.attribute_04); -- selectOnFocus',
'  v_custom_att      p_item.attribute_05%TYPE := (p_item.attribute_05); -- custom attribute',
'BEGIN',
'  v_reverse         := case when v_reverse <> ''N'' then ''true'' else ''false'' end;',
'  v_clearIfNotMatch := case when v_clearIfNotMatch <> ''N'' then ''true'' else ''false'' end;',
'  v_selectOnFocus   := case when v_selectOnFocus <> ''N'' then ''true'' else ''false'' end;',
'  if v_custom_att is not null then',
'    v_custom_att := '',''||v_custom_att;',
'  end if;    ',
'  -- Load JS Libraries',
'  apex_javascript.add_library (p_name => ''jquery.mask'', p_directory => p_plugin.file_prefix, p_version => null, p_check_to_add_minified => true);',
'  ',
'  -- Input item',
'    sys.htp.prn(''<input'');',
'    sys.htp.prn('' type="text"'');',
'    sys.htp.prn('' id="'' || p_item.name || ''"'');',
'    sys.htp.prn('' name="'' || p_item.name || ''"'');',
'    sys.htp.prn('' class="apex-item-text ''|| p_item.element_css_classes || ''"'');',
'    sys.htp.prn(case when p_item.is_required then '' required'' else null end);',
'    sys.htp.prn('' size="'' || p_item.element_width || ''"'');',
'    sys.htp.prn('' placeholder="'' || p_item.placeholder || ''"'');',
'    sys.htp.prn('' value="'');',
'    sys.htp.prn(p_param.value);',
'    sys.htp.prn(''"'');',
'    sys.htp.prn(''/>'');',
'',
'    -- Run onLoad code',
'    apex_javascript.add_onload_code (p_code =>',
'        ''$(''''#''|| p_item.name ||'''''').mask(''''''||v_mask||'''''',{                              ',
'	                                 reverse: ''||v_reverse||'',',
'									 clearIfNotMatch: ''||v_clearIfNotMatch||'',',
'									 selectOnFocus: ''||v_selectOnFocus||''',
'                                     ''||v_custom_att||''',
'                                     }',
'		);''',
'      );',
'END;'))
,p_api_version=>2
