'    function retorna_label_coluna(p_tag_label varchar2)  ',
'    return varchar2 ',
'    is ',
'        e_erro_formato exception; ',
'        l_ponto_posicao number; ',
'        l_nome_tabela varchar(255); ',
'        l_nome_campo varchar(255); ',
'        l_label varchar(255); ',
'    begin ',
'        l_ponto_posicao := INSTR(p_tag_label, ''.''); ',
'        if l_ponto_posicao = 0 then ',
'            l_ponto_posicao := INSTR(p_tag_label, ''$''); ',
'        end if; ',
'        if l_ponto_posicao > 0 then ',
'            l_nome_tabela := SUBSTR(p_tag_label, 1, l_ponto_posicao - 1); ',
'            l_nome_campo := SUBSTR(p_tag_label, l_ponto_posicao + 1); ',
'            if SUBSTR(upper(l_nome_campo), -2) = ''_L'' then ',
'                l_nome_campo := SUBSTR(l_nome_campo, 1, LENGTH(l_nome_campo) - 2); ',
'            end if; ',
'            select label into l_label from srv_entidade_coluna a  ',
'            join srv_entidade b on b.id = a.id_entidade ',
'            where upper(b.nome_entidade) = upper(l_nome_tabela) and upper(a.nome_coluna_banco) = upper(l_nome_campo); ',
'        else ',
'            raise e_erro_formato; ',
'        end if; ',
' ',
'        return l_label; ',
' ',
'        exception ',
'            when e_erro_formato then     ',
'                raise_application_error(-20695,''Formato da TAG invalido: ''||p_tag_label);  ',
'   end; ',
