'/',
'create or replace package body bo_mpd_cadastro_compartilhado is ',
' -- ',
unistr(' -- Fun\00E7\00F5es CRUD '),
' -- ',
'    procedure inclui_registro( ',
'        p_nome_cadastro in mpd_cadastro_compartilhado.nome_cadastro%type, ',
'        p_id out mpd_cadastro_compartilhado.id%type, ',
'        p_pagina in number default 0, ',
'        p_resultado out clob, ',
'        p_resultado_mensagem out clob ',
'    )is ',
'    begin ',
'        v_pagina := p_pagina; ',
' /* Seta valores passados por parametros */ ',
'        v_new.nome_cadastro := p_nome_cadastro; ',
'        v_new.id_usuario_incluiu := pkg_util.retorna_id_usuario_logado; ',
'        v_new.data_inclusao := sysdate; ',
'        v_new.id_usuario_alterou := pkg_util.retorna_id_usuario_logado; ',
'        v_new.data_alteracao := sysdate; ',
' /* Executa rotinas antes de incluir */ ',
'        p_resultado_mensagem := antes_incluir; ',
'        if p_resultado_mensagem is not null then ',
'            raise pk_exceptions.e_erro_antes_incluir; ',
'        end if; ',
' /* Executa insert */ ',
' ',
'        insert into mpd_cadastro_compartilhado ( ',
'            nome_cadastro, ',
'            id_usuario_incluiu, ',
'            data_inclusao, ',
'            id_usuario_alterou, ',
'            data_alteracao ',
'        ) values ( ',
'            v_new.nome_cadastro, ',
'            v_new.id_usuario_incluiu, ',
'            v_new.data_inclusao, ',
'            v_new.id_usuario_alterou, ',
'            v_new.data_alteracao ',
'        ) returning id into p_id; ',
'        v_new.id := p_id; ',
' /* Executa rotinas depois de incluir */ ',
'        p_resultado_mensagem := depois_incluir; ',
'        if p_resultado_mensagem is not null then ',
'            raise pk_exceptions.e_erro_depois_incluir; ',
'        else ',
'            p_resultado := pk_constants.c_msg_success; ',
'            p_resultado_mensagem := pkg_api_mensagem.mensagem( ',
'                p_codigo_mensagem => ''SUS-INC-001'', ',
'                p_parametros_mensagem => ''Cadastro compartilhado'' ',
'            ); ',
'        end if; ',
'    exception ',
'        when pk_exceptions.e_erro_antes_incluir then ',
'            p_resultado := pk_constants.c_msg_error; ',
'            pkg_api_mensagem.exibir_mensagem(p_resultado_mensagem); ',
'        when pk_exceptions.e_erro_depois_incluir then ',
'            p_resultado := pk_constants.c_msg_error; ',
'            pkg_api_mensagem.exibir_mensagem(p_resultado_mensagem); ',
'        when others then ',
'            p_resultado := pk_constants.c_msg_error; ',
'            pkg_api_mensagem.exibir_mensagem( pkg_api_mensagem.busca_mensagem( ',
'                p_codigo_mensagem => ''ERR-INC-001'', ',
'                p_exibicao => pk_constants.c_exibe_pagina, ',
'                p_parametros_mensagem => ''registro;'' ',
'                                         ||sqlerrm ',
'            )); ',
'    end inclui_registro; ',
' ',
'    procedure altera_registro( ',
'        p_nome_cadastro in mpd_cadastro_compartilhado.nome_cadastro%type, ',
'        p_id in mpd_cadastro_compartilhado.id%type, ',
'        p_data_alteracao in mpd_cadastro_compartilhado.data_alteracao%type, ',
'        p_pagina in number default 0, ',
'        p_resultado out clob, ',
'        p_resultado_mensagem out clob ',
'    )is ',
'    begin ',
' /* Verifica se o id informado existe */ ',
'        p_resultado_mensagem := verifica_registro(p_id); ',
'        if p_resultado_mensagem is not null then ',
'            raise pk_exceptions.e_erro_id; ',
'        end if; ',
unistr(' /* Verifica se o registro atual est\00E1 desatualizado */ '),
' ',
'        if data_alteracao(p_id) <> p_data_alteracao then ',
'            raise pk_exceptions.e_erro_data_alteracao; ',
'        end if; ',
' ',
'        v_pagina := p_pagina; ',
' /* Seta valores passados por parametros */ ',
'        v_new.nome_cadastro := p_nome_cadastro; ',
'        v_new.id := p_id; ',
'        v_new.id_usuario_alterou := pkg_util.retorna_id_usuario_logado; ',
'        v_new.data_alteracao := sysdate; ',
' /* Executa rotinas antes de alterar */ ',
'        p_resultado_mensagem := antes_alterar; ',
'        if p_resultado_mensagem is not null then ',
'            raise pk_exceptions.e_erro_antes_alterar; ',
'        end if; ',
' /* Executa insert */ ',
'        update mpd_cadastro_compartilhado ',
'        set ',
'            nome_cadastro = v_new.nome_cadastro, ',
'            id_usuario_alterou = v_new.id_usuario_alterou, ',
'            data_alteracao = v_new.data_alteracao ',
'        where ',
'            id = p_id; ',
' /* Executa rotinas depois de alterar */ ',
'        p_resultado_mensagem := depois_alterar; ',
'        if p_resultado_mensagem is not null then ',
'            raise pk_exceptions.e_erro_depois_alterar; ',
'        else ',
'            p_resultado := pk_constants.c_msg_success; ',
'            p_resultado_mensagem := pkg_api_mensagem.mensagem( ',
'                p_codigo_mensagem => ''SUS-ALT-001'', ',
'                p_parametros_mensagem => ''Cadastro compartilhado'' ',
'            ); ',
'        end if; ',
'    exception ',
'        when pk_exceptions.e_erro_id then ',
'            p_resultado := pk_constants.c_msg_error; ',
'            pkg_api_mensagem.exibir_mensagem(p_resultado_mensagem); ',
'        when pk_exceptions.e_erro_data_alteracao then ',
'            p_resultado := pk_constants.c_msg_error; ',
'            pkg_api_mensagem.exibir_mensagem(pkg_api_mensagem.busca_mensagem( ',
'                p_codigo_mensagem => ''ERR-DES-001'', ',
'                p_exibicao => pk_constants.c_exibe_pagina ',
'            )); ',
'        when pk_exceptions.e_erro_antes_alterar then ',
'            p_resultado := pk_constants.c_msg_error; ',
'            pkg_api_mensagem.exibir_mensagem(p_resultado_mensagem); ',
'        when pk_exceptions.e_erro_depois_alterar then ',
'            p_resultado := pk_constants.c_msg_error; ',
'            pkg_api_mensagem.exibir_mensagem(p_resultado_mensagem); ',
